// Tomamos el primer item de entrada (el del GET anterior)
const input = $input.first();
const board = input.json;
const boardData = items[0].json;
const rows = [];

// Recorremos columnas y cards
for (const column of board.columns ?? []) {
  for (const card of column.cards ?? []) {
    rows.push({
      id: card.id,
      title: card.title,
      description: card.description || "",
      column: column.name,
      createdAt: card.createdAt,
    });
  }
}

// Construimos el CSV
const header = "ID,TÃ­tulo,DescripciÃ³n,Columna,Fecha de creaciÃ³n\n";
const body = rows
  .map(
    (r) =>
      `${r.id},"${r.title.replace(/"/g, '""')}","${r.description.replace(/"/g, '""')}",${r.column},${r.createdAt}`
  )
  .join("\n");

const csv = header + body;

// Convertimos a base64 para adjuntar
const csvBase64 = Buffer.from(csv, "utf8").toString("base64");

// Retornamos con JSON + binario
return {
  json: {
    email: boardData.createdBy.email ?? "fallback@example.com",
    filename: `backlog_${board.name}.csv`,
  },
  binary: {
    data: {
      data: csvBase64,
      mimeType: "text/csv",
      fileName: `backlog_${board.name}.csv`,
    },
  },
};
